videoModal = "<!-- MODAL POPUP WINDOW AND SCRIPT --><div class='modal fade' id='modal-video_01' tabindex='-1' role='dialog' aria-labelledby='modal-video-label'><div class='modal-dialog' role='document'><div class='modal-content'><div class='modal-header'><button type='button' class='close' data-dismiss='modal' aria-label='Close'><span aria-hidden='true'>&times;</span></button></div><div class='modal-body'><div class='modal-video'><div class='embed-responsive embed-responsive-16by9'><iframe id='same_day_delivery_video' class='embed-responsive-item' width='1170'allow='accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture' src=''webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe></div></div></div></div></div></div><script>window.addEventListener('load',function () {if (window.jQuery) {var trigger = $('#mobile_modal');trigger.click(function () {var theModal = $(this).data('target'),videoSRC = $(this).attr('data-theVideo'),videoSRCauto =videoSRC +'?rel=0&vq=1080&controls=1&autoplay=1&modestbranding=1&showinfo=0&showinfo=1';$(theModal + ' iframe').attr('src', videoSRCauto);$(theModal + ' button.close').click(function () {$(theModal + ' iframe').attr('src', videoSRC);});$('#modal-video_01').on('hidden.bs.modal', function (e) {$(theModal + ' iframe').attr('src', videoSRC);});});}},false);</script>"


